function assignClassStudents(t,e){t.preventDefault(),selectedStudents=[],window.confirm("Are you sure?")&&(id=$(e).data("id"),$(".assignedStudents input[type=checkbox]:checked").each(function(){selectedStudents.push($(this).attr("id"))}),$.post("/class/"+id+"/manage_students.json",{students_ids:selectedStudents},function(t){window.location="/jkci_classes/"+t.id}))}function createClassExam(t,e){t.preventDefault(),selectedPoints=[],window.confirm("Are you sure?")&&(id=$(e).data("id"),$(".dailyTeach input[type=checkbox]:checked").each(function(){selectedPoints.push($(this).attr("id"))}),window.location="/exams/new?&jkci_class_id="+id+"&dtp="+selectedPoints)}function classFilterByBatch(t){t.preventDefault(),batch_id=$("#filter_batch_class").val(),subject_id=$("#filter_subject_class").val(),$.get("/jkci_class/filter_class/batch?&btch_id="+batch_id+"&subject_id="+subject_id,function(t){$(".jkciClassTable tbody").html(t.html),$(".paginationDiv").html(t.pagination_html)})}var selectedStudents=[],selectedPoints=[];